import React, { useState } from 'react';
import { createReminder } from '../api';
import { subscribeUser } from '../push';

export default function ReminderForm({ onSaved }) {
  const [title, setTitle] = useState('');
  const [details, setDetails] = useState('');
  const [dateTime, setDateTime] = useState('');
  const [channels, setChannels] = useState(['browser']);
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');

  const toggleChannel = (ch) => {
    setChannels(prev => prev.includes(ch) ? prev.filter(x => x !== ch) : [...prev, ch]);
  };

  const submit = async (e) => {
    e.preventDefault();
    if (!title || !dateTime || (!channels.length)) return alert('Please fill required fields');

    const payload = {
      userEmail: email,
      userPhone: phone,
      title,
      details,
      dateTime: new Date(dateTime).toISOString(),
      channels,
      repeat: 'none'
    };

    try {
      await createReminder(payload);
      // if browser push included, request permission and subscribe
      if (channels.includes('browser')) {
        if (Notification.permission !== 'granted') {
          await Notification.requestPermission();
        }
        if (Notification.permission === 'granted') {
          await subscribeUser(email);
        }
      }
      setTitle(''); setDetails(''); setDateTime('');
      onSaved && onSaved();
    } catch (err) {
      console.error(err);
      alert('Save failed');
    }
  };

  return (
    <form onSubmit={submit}>
      <div>
        <label>Title*</label>
        <input value={title} onChange={e=>setTitle(e.target.value)} required />
      </div>
      <div>
        <label>Details</label>
        <input value={details} onChange={e=>setDetails(e.target.value)} />
      </div>
      <div>
        <label>Date & Time*</label>
        <input type="datetime-local" value={dateTime} onChange={e=>setDateTime(e.target.value)} required />
      </div>
      <div>
        <label>Your email</label>
        <input value={email} onChange={e=>setEmail(e.target.value)} />
      </div>
      <div>
        <label>Your phone</label>
        <input value={phone} onChange={e=>setPhone(e.target.value)} />
      </div>
      <div>
        <label>Channels</label>
        <label><input type="checkbox" checked={channels.includes('browser')} onChange={()=>toggleChannel('browser')}/> Browser</label>
        <label><input type="checkbox" checked={channels.includes('email')} onChange={()=>toggleChannel('email')}/> Email</label>
        <label><input type="checkbox" checked={channels.includes('sms')} onChange={()=>toggleChannel('sms')}/> SMS</label>
      </div>
      <button type="submit">Save Reminder</button>
    </form>
  );
}
